import "./App.css";
import { useState, useEffect } from "react";
import Login from "../Login/login";
import Nav from "../Nav/Nav";
import HomePage from "../HomePage/HomePage";
import { signInWithGoogle } from "../Firebase/google";

function App() {
  const apiKey = "ef7fd608-e4ba-4e7d-b63c-8fcc4fd8f9f1";
  const [user, setUser] = useState();
  const [search, setSearch] = useState();

  console.log(user);

  async function handleGoogle() {
    const user = await signInWithGoogle();
    const newUser = {
      userName: user.displayName,
      email: user.email,
      photo: user.photoURL,
      id: user.uid,
    };
    setUser(newUser);
  }

  useEffect(() => {
    async function getNews() {
      let response = await fetch(
        `https://content.guardianapis.com/search?${search}api-key=${apiKey}`
      );
      let data = await response.json();
      console.log(data);
    }
  }, [search]);

  return (
    <div className="App">
      {!user && <Login handleClick={handleGoogle} />}
      {user && <Nav user={user} />}
      {user && <HomePage user={user} />}
    </div>
  );
}

export default App;
